import React from "react";
import { useState, useEffect } from "react";
import Sidebar from "../Sidebar/Sidebar";

const Product = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(
          "http://localhost:4000/product/allproduct"
        );
        const json = await response.json();
        setData(json);
      } catch (err) {
        setError(err);
      }
      setLoading(false);
    };
    fetchData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;
  if (!data) return null;
  return (
    <div className="flex">
      <Sidebar />
      <main className="w-full p-10">
        <h3 className="font-extrabold">Products</h3>
        <div className="product-container w-full">
          <table class="table-auto w-full text-center">
            <thead>
              <tr class="">
                <th class="px-4 py-2">PRODUCT NAME</th>
                <th class="px-4 py-2">PRICE</th>
                <th class="px-4 py-2">CATEGORY</th>
                <th class="px-4 py-2">STOCK</th>
                <th class="px-4 py-2">PUBLISHED ON</th>
              </tr>
            </thead>
            <tbody>
              {data.map((product, index) => (
                <tr>
                  <td className="flex items-center border">
                    <img src={product.image} alt="" className="h-10" />
                    <a
                      hre="#"
                      className="ml-2 text-blue-700 hover:text-black cursor-pointer"
                    >
                      {product.name}
                    </a>
                  </td>
                  <td className="border">{product.price}</td>
                  <td>Gay</td>
                  <td>{product.stock}</td>
                  <td>{product.createdAt}</td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
};
export default Product;
