import React from "react";
import { useState, useEffect } from "react";
import moment from "moment";
import Switch from "@mui/material/Switch";
import Sidebar from "../Sidebar/Sidebar";
import AddProduct from "./addProduct";

import "./style.css";

import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";
import { Link } from "react-router-dom";

const Product = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);
  const [sortBy, setSortBy] = useState(null);
  const [sortOrder, setSortOrder] = useState("asc");

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(
          "http://localhost:4000/product/allproduct"
        );
        const json = await response.json();
        setData(json);
      } catch (err) {
        setError(err);
      }
      setLoading(false);
    };
    fetchData();
  }, []);

  if (loading)
    return (
      <div className="text-center w-full h-screen flex items-center justify-center">
        <div role="status">
          <svg
            aria-hidden="true"
            className="inline w-10 h-10 mr-2 text-gray-200 animate-spin dark:text-gray-600 fill-blue-600"
            viewBox="0 0 100 101"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            <path
              d="M100 50.5908C100 78.2051 77.6142 100.591 50 100.591C22.3858 100.591 0 78.2051 0 50.5908C0 22.9766 22.3858 0.59082 50 0.59082C77.6142 0.59082 100 22.9766 100 50.5908ZM9.08144 50.5908C9.08144 73.1895 27.4013 91.5094 50 91.5094C72.5987 91.5094 90.9186 73.1895 90.9186 50.5908C90.9186 27.9921 72.5987 9.67226 50 9.67226C27.4013 9.67226 9.08144 27.9921 9.08144 50.5908Z"
              fill="currentColor"
            />
            <path
              d="M93.9676 39.0409C96.393 38.4038 97.8624 35.9116 97.0079 33.5539C95.2932 28.8227 92.871 24.3692 89.8167 20.348C85.8452 15.1192 80.8826 10.7238 75.2124 7.41289C69.5422 4.10194 63.2754 1.94025 56.7698 1.05124C51.7666 0.367541 46.6976 0.446843 41.7345 1.27873C39.2613 1.69328 37.813 4.19778 38.4501 6.62326C39.0873 9.04874 41.5694 10.4717 44.0505 10.1071C47.8511 9.54855 51.7191 9.52689 55.5402 10.0491C60.8642 10.7766 65.9928 12.5457 70.6331 15.2552C75.2735 17.9648 79.3347 21.5619 82.5849 25.841C84.9175 28.9121 86.7997 32.2913 88.1811 35.8758C89.083 38.2158 91.5421 39.6781 93.9676 39.0409Z"
              fill="currentFill"
            />
          </svg>
          <span className="sr-only">Loading...</span>
        </div>
      </div>
    );
  if (error) return <p>Error: {error.message}</p>;
  if (!data) return null;

  const handleSortClick = (column) => {
    if (sortBy === column) {
      setSortOrder(sortOrder === "asc" ? "desc" : "asc");
    } else {
      setSortBy(column);
      setSortOrder("asc");
    }
  };
  const sortedData = [...data].sort((a, b) => {
    if (sortBy === "name") {
      if (sortOrder === "asc") {
        return a.name.localeCompare(b.name);
      } else {
        return b.name.localeCompare(a.name);
      }
    } else if (sortBy === "price") {
      if (sortOrder === "asc") {
        return a.price - b.price;
      } else {
        return b.price - a.price;
      }
    } else if (sortBy === "createdAt") {
      if (sortOrder === "asc") {
        return new Date(a.createdAt) - new Date(b.createdAt);
      } else {
        return new Date(b.createdAt) - new Date(a.createdAt);
      }
    }
  });

  return (
    <main className="w-full p-10 h-screen overflow-scroll">
      <h3 className="font-extrabold my-5">Products</h3>
      <AddProduct />
      <div className="product-container w-full">
        <table className="w-full">
          <thead className="bg-gray-500 text-white border">
            <tr>
              <th
                className="py-2 flex justify-center"
                onClick={() => handleSortClick("name")}
              >
                Name
                <div className="indicator flex flex-col ml-4 -space-y-2">
                  <i class="fa-solid fa-chevron-up"></i>
                  <i class="fa-solid fa-chevron-down"></i>
                </div>
              </th>
              <th onClick={() => handleSortClick("price")}>Price</th>
              <th>Type</th>
              <th>Stock</th>
              <th onClick={() => handleSortClick("createdAt")}>Created At</th>
              <th>Status</th>
              <th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {sortedData.map((product, index) => (
              <tr className="">
                <td className="border flex items-center p-1">
                  <img
                    src={product.image}
                    alt=""
                    className="h-10 w-10 object-contain"
                  />
                  <Link
                    to="#"
                    className="ml-4 text-blue-700 hover:text-black cursor-pointer"
                  >
                    {product.name}
                  </Link>
                </td>
                <td className="border">${product.price}</td>
                <td className="border">Shirt</td>
                <td className="border">{product.stock}</td>
                <td className="border">
                  {moment(product.createdAt).format("MMMM Do YYYY, h:mm:ss a")}
                </td>
                <td className="border">
                  <Switch defaultChecked />
                </td>
                <td className="border">
                  <button className="  text-black text-sm p-2 rounded">
                    <EditIcon />
                  </button>
                  <button className="  text-red-600 text-sm p-2 rounded">
                    <DeleteIcon />
                  </button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </main>
  );
};
export default Product;
