import React from "react";
import { Link } from "react-router-dom";
import Carousel from "react-bootstrap/Carousel";

//image
import hero1 from "../../../Asset/img/hero/hero-1.jpg";
import hero2 from "../../../Asset/img/hero/hero-2.jpg";

function Hero() {
  return (
    <Carousel>
      <Carousel.Item>
        <img className="d-block w-100" src={hero1} alt="First slide" />
        <Carousel.Caption className="text-left">
          <h6>Summer Collection</h6>
          <h2>
            Fall - Winter <br /> Collections 2030
          </h2>
          <p>
            A specialist label creating luxury essentials. Ethically crafted
            with an unwavering commitment to exceptional quality.
          </p>
          <Link to="#" class="primary-btn">
            Shop now <span class="arrow_right"></span>
          </Link>
        </Carousel.Caption>
      </Carousel.Item>
      <Carousel.Item>
        <img className="d-block w-100" src={hero2} alt="Second slide" />

        <Carousel.Caption>
          <h3>Second slide label</h3>
          <p>Lorem ipsum dolor sit amet, consectetur adipiscing elit.</p>
        </Carousel.Caption>
      </Carousel.Item>
    </Carousel>
  );
}

export default Hero;
