import React from "react";
import { useState, useEffect } from "react";
import Sidebar from "../Sidebar/Sidebar";

const Product = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(
          "http://localhost:4000/product/allproduct"
        );
        const json = await response.json();
        setData(json);
      } catch (err) {
        setError(err);
      }
      setLoading(false);
    };
    fetchData();
  }, []);

  //if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;
  if (!data) return null;
  return (
    <main className="w-full p-10">
      <h3 className="font-extrabold">Products</h3>
      <div className="product-container w-full">
        <table class="table-auto w-full text-center">
          <thead>
            <tr class="bg-gray-800 text-white">
              <th class="px-4 py-2">PRODUCT NAME</th>
              <th class="px-4 py-2">PRICE</th>
              <th class="px-4 py-2">CATEGORY</th>
              <th class="px-4 py-2">STOCK</th>
              <th class="px-4 py-2">PUBLISHED ON</th>
            </tr>
          </thead>
          <tbody>
            {data.map((product, index) => (
              <tr className="">
                <td className="p-2 flex items-center border">
                  <img
                    src={product.image}
                    alt=""
                    className="h-10 w-10 object-contain"
                  />
                  <a
                    hre="#"
                    className="ml-4 text-blue-700 hover:text-black cursor-pointer"
                  >
                    {product.name}
                  </a>
                </td>
                <td className="border">${product.price}</td>
                <td className="border">{product.category}</td>
                <td className="border">{product.stock}</td>
                <td className="border">{product.createdAt}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </main>
  );
};
export default Product;
