import React from "react";
import { Link } from "react-router-dom";

import "./style.css";

import banner1 from "../../../Asset/img/banner/banner-1.jpg";
import banner2 from "../../../Asset/img/banner/banner-2.jpg";
import banner3 from "../../../Asset/img/banner/banner-3.jpg";

const Banner = () => {
  return (
    <section className="w-full flex justify-center">
      <div className="banner container bg-white p-4 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3  gap-4">
        {/* Men */}
        <div className="h-full relative men-col overflow-hidden ">
          <img
            src={banner1}
            alt=""
            className="w-full h-full object-cover ease-linear duration-100"
          />
          <a
            href="#"
            className="absolute bottom-4 left-1/2 -translate-x-1/2 flex justify-center items-center h-12 w-36 bg-white text-black hover:bg-black hover:text-white ease-linear duration-100"
          >
            <span className="text-inherit">Men</span>
          </a>
        </div>
        {/* Women */}
        <div className="h-full relative men-col overflow-hidden ">
          <img
            src={banner1}
            alt=""
            className="w-full h-full object-cover ease-linear duration-100"
          />
          <a
            href="#"
            className="absolute bottom-4 left-1/2 -translate-x-1/2 flex justify-center items-center h-12 w-36 bg-white text-black hover:bg-black hover:text-white ease-linear duration-100"
          >
            <span className="text-inherit">Men</span>
          </a>
        </div>
        <div className="lg:col-span-1 col-span-2 flex lg:block gap-4 h-full ">
          {/* Child */}
          <div className="w-full h-1/2 bg-red-600">Child</div>
          {/* Accessories */}
          <div className="w-full h-1/2 bg-blue-600">Accessories</div>
        </div>
      </div>
    </section>
  );
};

export default Banner;
