import React from "react";
import { useState } from "react";
import "animate.css";
//icon

import DoneIcon from "@mui/icons-material/Done";
import CloseIcon from "@mui/icons-material/Close";
import DeleteIcon from "@mui/icons-material/Delete";

const DeleteProduct = (props) => {
  const [showModal, setShowModal] = useState(false);
  const [showAlert, setShowAlert] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");

  const handleDelete = async (e) => {
    e.preventDefault(); // prevent form submission
    setLoading(true);
    try {
      const response = await axios.delete(
        `http://localhost:4000/product/delete/${props.proId}`
      );
      console.log(response.data);
      setLoading(false);
      setShowModal(false);
    } catch (error) {
      console.error(error);
      setLoading(false);
    }
  };

  return (
    <>
      <button
        className="text-red-600 text-sm p-2 rounded"
        onClick={() => setShowModal(true)}
      >
        <DeleteIcon />
      </button>
      {/* {showAlert ? (
        <div
          class="flex float-right p-4 mb-4 text-sm text-green-800 rounded-lg bg-green-50 border border-green-600"
          role="alert"
        >
          <span class="sr-only">Info</span>
          <div>
            <DoneIcon />
            <span class="font-medium mx-2">{errorMsg}</span>
            <CloseIcon
              className="cursor-pointer"
              onClick={() => setShowAlert(false)}
            />
          </div>
        </div>
      ) : null} */}
      {showModal ? (
        <>
          <div
            className={`justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none animate__animated ${
              showModal ? "animate__bounceIn" : "animate__bounceOut"
            }`}
          >
            <div className="relative w-1/2 my-10 mx-auto max-w-sm">
              <div className="py-10 border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
                <h5>Do you want to delete this product?</h5>
                <form action="" className="mt-10">
                  <div className="form-items space-x-8">
                    <button
                      class="bg-red-500 hover:bg-red-700 text-white font-bold py-2 px-4 rounded"
                      onClick={() => handleDelete(props.proId)}
                    >
                      Yes
                    </button>
                    <button
                      class="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded"
                      onClick={() => setShowModal(false)}
                    >
                      Cancel
                    </button>
                  </div>
                </form>
              </div>
            </div>
          </div>
          <div className={`opacity-25 fixed inset-0 z-40 bg-black `}></div>
        </>
      ) : null}
    </>
  );
};

export default DeleteProduct;
