import React from "react";
import { useState, useEffect } from "react";
import Sidebar from "../Sidebar/Sidebar";

const Product = () => {
  const [data, setData] = useState(null);
  const [error, setError] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    const fetchData = async () => {
      setLoading(true);
      setError(null);
      try {
        const response = await fetch(
          "http://localhost:4000/product/allproduct"
        );
        const json = await response.json();
        setData(json);
      } catch (err) {
        setError(err);
      }
      setLoading(false);
    };
    fetchData();
  }, []);

  if (loading) return <p>Loading...</p>;
  if (error) return <p>Error: {error.message}</p>;
  if (!data) return null;
  return (
    <div className="flex">
      <Sidebar />
      <main className="w-full p-10">
        <h3 className="font-extrabold">Products</h3>
        <div className="product-container w-full">
          <table class="table-auto w-full text-left">
            <thead>
              <tr class="">
                <th class="px-4 py-2">PRODUCT NAME</th>
                <th class="px-4 py-2">PRICE</th>
                <th class="px-4 py-2">CATEGORY</th>
                <th class="px-4 py-2">STOCK</th>
                <th class="px-4 py-2">PUBLISHED ON</th>
              </tr>
            </thead>
            <tbody>
              {data.map((product, index) => (
                <div className="col-span-1 product-col w-62  overflow-hidden">
                  <div className="image relative overflow-hidden">
                    <img
                      src={product.image}
                      alt=""
                      className="w-full object-cover h-72"
                    />
                    <div className="hover-nav w-full h-full absolute flex flex-col justify-center items-center">
                      <div className="hover-nav-btn">
                        <i class="fa-solid fa-cart-shopping"></i>
                        <a href="">Add to Cart</a>
                      </div>
                      <div className="hover-nav-btn">
                        <i class="fa-solid fa-eye"></i>
                        <a href="">Quick View</a>
                      </div>
                    </div>
                  </div>
                  <div className="product-des py-2">
                    <h3 className="text-lg font-semibold">{product.name}</h3>
                    <a className="price text-base text-gray-700" href="#">
                      {product.price}
                    </a>
                  </div>
                </div>
              ))}
            </tbody>
          </table>
        </div>
      </main>
    </div>
  );
};
export default Product;
