import React from "react";
import { useState } from "react";

//icon

import DoneIcon from "@mui/icons-material/Done";
import CloseIcon from "@mui/icons-material/Close";
import DeleteIcon from "@mui/icons-material/Delete";

const DeleteProduct = () => {
  const [showModal, setShowModal] = useState(false);
  const [showAlert, setShowAlert] = useState(false);
  const [errorMsg, setErrorMsg] = useState("");

  return (
    <>
      <button
        className="text-red-600 text-sm p-2 rounded"
        onClick={() => setShowModal(true)}
      >
        <DeleteIcon />
      </button>
      {showAlert ? (
        <div
          class="flex float-right p-4 mb-4 text-sm text-green-800 rounded-lg bg-green-50 border border-green-600"
          role="alert"
        >
          <span class="sr-only">Info</span>
          <div>
            <DoneIcon />
            <span class="font-medium mx-2">{errorMsg}</span>
            <CloseIcon
              className="cursor-pointer"
              onClick={() => setShowAlert(false)}
            />
          </div>
        </div>
      ) : null}
      {showModal ? (
        <>
          <div className="justify-center items-center flex overflow-x-hidden overflow-y-auto fixed inset-0 z-50 outline-none focus:outline-none">
            <div className="relative w-1/2 my-10 mx-auto max-w-xl">
              <div className="border-0 rounded-lg shadow-lg relative flex flex-col w-full bg-white outline-none focus:outline-none">
                <h2>Do you want to delete this product?</h2>
              </div>
            </div>
          </div>
          <div className="opacity-25 fixed inset-0 z-40 bg-black"></div>
        </>
      ) : null}
    </>
  );
};

export default DeleteProduct;
